import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const RecipeDetail = () => {
    const { id } = useParams(); // Получаем ID из URL
    const [recipe, setRecipe] = useState(null); // Состояние для хранения рецепта
    const [loading, setLoading] = useState(true); // Состояние загрузки
    const [error, setError] = useState(null); // Состояние ошибки

    useEffect(() => {
        const fetchRecipe = async () => {
            try {
                const response = await fetch(`http://127.0.0.1:8000/api/recipe/${id}/`);
                if (!response.ok) {
                    throw new Error('Ошибка при загрузке рецепта');
                }
                const data = await response.json();
                setRecipe(data);
            } catch (err) {
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };

        fetchRecipe();
    }, [id]);

    if (loading) {
        return <div>Загрузка...</div>;
    }

    if (error) {
        return <div>Ошибка: {error}</div>;
    }

    if (!recipe) {
        return <div>Рецепт не найден</div>;
    }

    return (
        <div>
            <h1>Recipes</h1>
            <ul>
                {recipes.map((recipe) => (
                    <li key={recipe.id}>
                        <h2>{recipe.name}</h2>
                        <p>{recipe.description}</p>
                        
                    </li>
                    
                ))}
                <Link to='/'>Назад на главную</Link>
            </ul>
        </div>
    );
};

export default RecipeDetail;
