// src/components/Home.js
import React from 'react';
import { Link } from 'react-router-dom';

const Home = () => {
  return (
    <div>
      <h1 className='category'>Сайт с вашими любимыми блюдами</h1>
      <div class="tenor-gif-embed" data-postid="20897276" data-share-method="host" data-aspect-ratio="1.77778" data-width="100%"><a href="https://tenor.com/view/eating-food-gif-20897276">Eating Food GIF</a>from <a href="https://tenor.com/search/eating-gifs">Eating GIFs</a></div> <script type="text/javascript" async src="https://tenor.com/embed.js"></script>
      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '50vh' }}>
        <img src="путь_к_вашему_gif.gif" alt="Описание GIF" style={{ maxWidth: '100%', maxHeight: '100%' }} />
      </div>
      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '50vh' }}>
        <button type="button" className="btn btn-info">
          <Link to="/categories" style={{ color: 'inherit', textDecoration: 'none' }}>Перейти к категориям блюд</Link>
        </button>
      </div>
    </div>
  );
};

export default Home;
